package practice;

public class ASCII {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a ='G';
		int ascii = a;
		float Ascii = ascii;
		double castascii =(float) a;
		System.out.println("The ASCII value of : " + a + ascii);
		System.out.println("The ASCII value of : " + a + castascii);
		System.out.println("The ASCII value of : " + a + Ascii);

	}

}
