package Task2;

public class Defaultvalue {
	static boolean a;
	static double b;
	static float c;
	static int d;
	static long e;
	static short f;
	static byte g;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Default value of boolean is: "+a);
		System.out.println("Default value of double is: "+b);
		System.out.println("Default value of float is: "+c);
		System.out.println("Default value of int is: "+d);
		System.out.println("Default value of long is: "+e);
		System.out.println("Default value of short is: "+f);
		System.out.println("Default value of byte is: "+g);


	}

}
